    <div>
    <div className="landing-page__p">
            <div >
          <Link to=  {`/todo/${id}`}/>
            <p> {item} </p>
            <Button size='small' onClick = { () => {
              //  dispatch(removeTodo({ id }));
          //  console.log(todo.description)
        }}> Remove </Button>

        <Button size='small' > Edit </Button>
            </div>
            <div >

