                  <Button size='small' onClick = { () => {
                    this.props.removeTodo(this.props.index)
                //  console.log(todo.description)
              }}> Remove </Button>

              <Button size='small' onClick ={this.handleEditing.bind(this)}> Edit </Button>

                  </div>
