import { getTodos } from './action/todos';

class Main extends Component {
constructor(){
  super()
}

  componentWillMount() {
    this.props.getTodos();
  }

  render() {
    return (
      <div>
        <h1>My Todo App</h1>

        <List />
      </div>
    )
  }
}



