import React,{Component} from 'react'
import Todo from './Todo'
import PropTypes from 'prop-types'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux';
import { getTodos } from '../action/todos';

import { Button,FormGroup,TextInput,OrderedList,ListItem } from "carbon-components-react";

class List extends Component {
  componentDidMount(){
      this.props.getTodos();
  }
  render() {
      let {actions} = this.props.todos;
    return actions.map(act => <Todo key={act._id} id={act._id} item={act.action} />);
  }
}

const mapStateToProps  = state => ({
    todos: state
});




export default connect(mapStateToProps)(List);
