import '../styles/app.scss'
import { Button,FormGroup,TextInput,OrderedList,ListItem } from "carbon-components-react";

const App = () => (
    <BrowserRouter>
        <div >
            <h1> My TODO List</h1>
            <Switch>
                <Route path="/" component={List} />


            </Switch>
        </div>
    </BrowserRouter>
);



